<script>
    function toggleMobileMenu() {
        var menu = document.getElementById("navbar-menu-mobile");
        if (menu.style.display === "block") {
            menu.style.display = "none";
        } else {
            menu.style.display = "block";
        }
    }

    window.addEventListener("resize", function() {
        if (window.innerWidth >= 992) {
            document.getElementById("navbar-menu").style.display = "block";
            document.getElementById("navbar-menu-mobile").style.display = "none";
        } else {
            document.getElementById("navbar-menu").style.display = "none";
            document.getElementById("navbar-menu-mobile").style.display = "none";
        }
    });

    window.addEventListener("load", function() {
        if (window.innerWidth >= 992) {
            document.getElementById("navbar-menu").style.display = "block";
            document.getElementById("navbar-menu-mobile").style.display = "none";
        } else {
            document.getElementById("navbar-menu").style.display = "none";
            document.getElementById("navbar-menu-mobile").style.display = "none";
        }
    });
</script>

