      http.open('GET', `../../../assets/profilePic/${contributors[i].profilePic}`, false);
      http.send();
      if (http.status === 200) {
        if (contributors[i].profilePic.includes("https[\w]+")) {
          imgpath = contributors[i].profilePic
        } else {
          imgpath = `../../../assets/profilePic/${contributors[i].profilePic}`;
        }

      } else {
        imgpath = "../../../assets/images/dummy_profile.png"
      }
      console.log(`../../../assets/profilePic/${contributors[i].profilePic}`, imgpath)

      var theNumber = noOfContributions === 1 ? `${noOfContributions} Contribution` : `${noOfContributions} Contributions`

      var contributorSection = '<div class="contributor-individual"><div class="contributor-info"><img src="' + imgpath + `" class="circle responsive-img">${contributors[i].fullName}</div><div class="no-of-contributions">${theNumber}</div></div>`

      document.getElementById("contributorsPage").innerHTML += contributorSection
    }</script><div class="left-content-headers show-on-medium-and-down hide-on-large-only" style="padding-top: 20px;"></div></div><div id="commentPage" class="column"><div class="divider"></div><div class="left-content-headers" style="padding: 20px 0 0 20px">Comments</div><div id="commento"></div><script defer="" src="https://comments.templesofindia.org/js/commento.js"></script></div></div><div id="ended"></div></div>
    
    
    
    
    
